INSERT INTO `js_recommendreason` VALUES (1,1,2,'6547567',0,0),(2,2,2,'43253456',0,0),(3,3,2,'565467reytery563456',0,0),(4,2,1,'《生活中的社会学》从社会生活人手生动地介绍了社会文化、社会角色、社会互动、社会群体、婚姻家庭等社会学的基本范畴，娓娓道来，社会学的轮廓初显。《生活中的社会学》适合于青年读者及对社会学有兴趣的社会大众。',1,0),(5,1,1,'城市社会学（urban sociology），又称都市社会学，以城市的区位、社会结构、社会组织、生活方式、社会心理、社会问题和社会发展规律等为主要研究对象的一门学科。',1,0),(6,3,1,'贺永顺所著的《社会管理探讨(均衡利益与制衡权力)》一共有十五章，从第一章到第十章，比较偏重于理论上的阐述。这一部分最重要的内容主要在第三、四、五章里，所阐述的基本观点可以概括为“权力利益”原则，即任何社会中的权力和利益都是相伴相生的，“利为权所求，权为利所依”，既没有不要“利”的权力，也没有不需“权\"的利益。换句话说，人们追逐权力的目的都是为了谋取利益，而保障人们自身利益的关键则是他们所拥有的权力。 \r\n',1,0),(7,1,4,'城市社会学（urban sociology），又称都市社会学，以城市的区位、社会结构、社会组织、生活方式、社会心理、社会问题和社会发展规律等为主要研究对象的一门学科。',0,0),(8,2,4,'《生活中的社会学》从社会生活人手生动地介绍了社会文化、社会角色、社会互动、社会群体、婚姻家庭等社会学的基本范畴，娓娓道来，社会学的轮廓初显。《生活中的社会学》适合于青年读者及对社会学有兴趣的社会大众。',0,0),(9,3,4,'贺永顺所著的《社会管理探讨(均衡利益与制衡权力)》一共有十五章，从第一章到第十章，比较偏重于理论上的阐述。这一部分最重要的内容主要在第三、四、五章里，所阐述的基本观点可以概括为“权力利益”原则，即任何社会中的权力和利益都是相伴相生的，“利为权所求，权为利所依”，既没有不要“利”的权力，也没有不需“权\"的利益。换句话说，人们追逐权力的目的都是为了谋取利益，而保障人们自身利益的关键则是他们所拥有的权力。 \r\n　　第二部分从第十一章到第十三章，内容主要是对中国和西方社会管理的历史进行回顾，并在简单比较的基础上对中国社会管理进行归纳。第十四章和第十五章，主要分析了中国社会管理的现状，并对其面临的主要社会问题进行了简单的讨论。',0,0),(10,1,3,'三批发布的书书籍',0,0),(11,2,3,'三批发布',0,1493024732);
